import React from "react";
import PropTypes from 'prop-types'; // prop의 타입을 검사해주는 패키지
import Link from 'next/link';
import { Menu, Input, Row, Col} from 'antd';

// 직접 antd에 가서 찾아서 쓰는 것이다. Input.Sear초 태그를 추가하니 정렬이 안 맞는데, 이런 부분은 실제 CSS를 덮어씌워서 해결할 수도 있다.
// Row, Col로 반응형 디자인을 한다. 24등분을 기준으로 한 열(행)에 얼마나 차지를 할 것인지를 결정한다.

const AppLayout = ({ children }) => {
  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item>
          <Link href='/'><a>노드버드</a></Link>
        </Menu.Item>
        <Menu.Item>
          <Link href='/profile'><a>프로필</a></Link>
        </Menu.Item>
        <Menu.Item>
          <Input.Search enterButton style={{verticalAlign: "middle"}}/>
        </Menu.Item>
        <Menu.Item>
          <Link href='/signup'><a>회원가입</a></Link>
        </Menu.Item>
      </Menu>
      <Row>
        <Col xs={24}  md={6}>

        </Col>

        <Col xs={24}  md={12}>

        </Col>
        <Col xs={24}  md={6}>
          
        </Col>
      </Row>
      {children}
    </div>
  )
}

AppLayout.PropTypes = {
  children: PropTypes.node.isRequired, // node는 React에서 화면에 그리는 모든 것들을 node라고 부른다.
}

export default AppLayout;
